mapper.update:
update scm_w_change
tj_rq=#{tjRq}, ny=#{ny}, fy_id=#{fyId}, kw=#{kw}, gys_id=#{gysId}, gys_name=#{gysName}, yp_lb=#{ypLb}, yp_bh=#{ypBh}, yp_cd=#{ypCd}, yp_mc=#{ypMc}, sl=#{sl}, dw=#{dw}, jh_old=#{jhOld}, jh_new=#{jhNew}, jh_je=#{jhJe}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from scm_w_change
where  1=1
     and scmWChange.id =#{ scmWChange.id} 
     and scmWChange.tj_rq like concat('%', #{ scmWChange.tjRq} ,'%')
     and scmWChange.ny like concat('%', #{ scmWChange.ny} ,'%')
     and scmWChange.fy_id like concat('%', #{ scmWChange.fyId} ,'%')
     and scmWChange.kw like concat('%', #{ scmWChange.kw} ,'%')
     and scmWChange.gys_id like concat('%', #{ scmWChange.gysId} ,'%')
     and scmWChange.gys_name like concat('%', #{ scmWChange.gysName} ,'%')
     and scmWChange.yp_lb like concat('%', #{ scmWChange.ypLb} ,'%')
     and scmWChange.yp_bh like concat('%', #{ scmWChange.ypBh} ,'%')
     and scmWChange.yp_cd like concat('%', #{ scmWChange.ypCd} ,'%')
     and scmWChange.yp_mc like concat('%', #{ scmWChange.ypMc} ,'%')
     and scmWChange.dw like concat('%', #{ scmWChange.dw} ,'%')
 and scmWChange.IS_DELETEMARK =#{ scmWChange.isDeletemark} 

    AND scmWChange.CREATE_TIME  >=   #{scmWChange.createTimeFrom}


    AND scmWChange.CREATE_TIME  <=   #{scmWChange.createTimeTo}


    AND scmWChange.MODIFY_TIME  >=   #{scmWChange.modifyTimeFrom}


    AND scmWChange.MODIFY_TIME  <=   #{scmWChange.modifyTimeTo}
