mapper.update:
update scm_b_gysfp
fp_hm=#{fpHm}, fp_bm=#{fpBm}, fprq=#{fprq}, fp_amount=#{fpAmount}, AuditCause=#{AuditCause}, COMMENTS=#{comments}, STATE=#{state}, mater_ID=#{materId}, FILE_ID=#{fileId}, GysName=#{GysName}, GYSACCOUNT=#{gysaccount}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select  *  from scm_b_gysfp

    INNER JOIN scm_b_userandarea A2 ON CONCAT(scm_b_gysfp.werks,scm_b_gysfp.lgort)  = A2.AreaID  AND A2.UserID=${scmBGysfp.userid}

where  1=1
     and scm_b_gysfp.id =#{ scmBGysfp.id} 
     and scm_b_gysfp.fp_hm like concat('%', #{ scmBGysfp.fpHm} ,'%')
     and scm_b_gysfp.fp_bm like concat('%', #{ scmBGysfp.fpBm} ,'%')
     and scm_b_gysfp.AuditCause like concat('%', #{ scmBGysfp.AuditCause} ,'%')
     and scm_b_gysfp.COMMENTS like concat('%', #{ scmBGysfp.comments} ,'%')
 and scm_b_gysfp.STATE =#{ scmBGysfp.state} 
     and scm_b_gysfp.mater_ID like concat('%', #{ scmBGysfp.materId} ,'%')
     and scm_b_gysfp.FILE_ID like concat('%', #{ scmBGysfp.fileId} ,'%')
     and scm_b_gysfp.GysName like concat('%', #{ scmBGysfp.GysName} ,'%')
     and scm_b_gysfp.GYSACCOUNT like concat('%', #{ scmBGysfp.gysaccount} ,'%')
 and (scm_b_gysfp.GYSACCOUNT = #{ scmBGysfp.keyword_gys} or scm_b_gysfp.GysName like concat('%', #{ scmBGysfp.keyword_gys} ,'%'))

 and scm_b_gysfp.IS_DELETEMARK =#{ scmBGysfp.isDeletemark} 

    AND scm_b_gysfp.CREATE_TIME  >=   #{scmBGysfp.createTimeFrom}


    AND scm_b_gysfp.CREATE_TIME  <=   #{scmBGysfp.createTimeTo}


    AND scm_b_gysfp.MODIFY_TIME  >=   #{scmBGysfp.modifyTimeFrom}


    AND scm_b_gysfp.MODIFY_TIME  <=   #{scmBGysfp.modifyTimeTo}
